INSERT INTO `vacancy` (`id`, `title`, `description-main`, `description`, `salary`) VALUES
(1, '<b>Оператор производственной линии</b>', 'Описание вакансии Nutricia Specialized Nutrition (SN) признанный эксперт в области питания на самых ответственных этапах жизни. Наш портфель это правильное и здоровое питание для малышей в течение первых месяцев жизни, для взрослых и детей...', '<br>Nutricia Specialized Nutrition (SN) – признанный эксперт в области питания на самых ответственных этапах жизни. Наш портфель – это правильное и здоровое питание для малышей в течение первых месяцев жизни, для взрослых и детей, которым нужна дополнительная нутритивная поддержка во время или после болезни. Все наши продукты имеют под собой глубокую научную базу и широко признаны медицинским и научным сообществом, они помогают человеку расти и жить здоровым.</br>\r\n\r\n<br>Обязанности:</br>\r\n\r\n<br>-засыпка компонентов, контроль их дозировки;</br>\r\n<br>-расфасовка готовой смеси в пачки;</br>\r\n<br>-упаковка в коробки;</br>\r\n<br>-Требования:</br>\r\n<br>-желание развиваться в производственной сфере;</br>\r\n<br>-готовность соблюдать санитарно-технические нормы и нормы безопасности;</br>\r\n<br>-опыт работы необязателен</br>\r\n\r\n<br>Условия:</br>\r\n\r\n<br>-Работа в компании-лидере в своей отрасли​;</br>\r\n\r\n<br>-Возможности для профессионального и карьерного роста;</br>\r\n\r\n<br>-Официальное оформление по ТК РФ;</br>\r\n\r\n<br>-Сменный график работы;</br>\r\n<br>-ДМС работника и несовершеннолетних детей;</br>\r\n<br>-Компенсация питания\r\n-Локальные выплаты и бенефиты\r\nРазвозка по городу;</br>', 'от 40 000 руб.'),
(2, '<b>Электромеханик</b>', 'Описание вакансии Nutricia (Specialized Nutrition, SN) признанный эксперт в области питания на самых ответственных этапах жизни. Наш портфель это правильное и здоровое питание для малышей в течение первых месяцев жизни, а также для взрослых и детей...', '<br>Nutricia (Specialized Nutrition, SN) – признанный эксперт в области питания на самых ответственных этапах жизни.\r\nНаш портфель – это правильное и здоровое питание для малышей в течение первых месяцев жизни, а также для взрослых и детей, которым нужна дополнительная нутритивная поддержка во время или после болезни. Все наши продукты имеют под собой глубокую научную базу и широко признаны медицинским и научным сообществом, они помогают человеку расти и жить здоровым.​</br>\r\n\r\n<br>Обязанности:</br>\r\n\r\n<br>Обеспечение исправного состояния, безаварийную и надежную работу обслуживаемых устройств и оборудования, правильную их эксплуатацию;<.br>\r\n\r\n<br>Своевременный качественный ремонт и модернизацию в соответствии с инструкциями по техническому обслуживанию, утвержденными чертежами и схемами, действующими техническими условиями и нормами;</br>\r\n\r\n<br>Проведение обхода закрепленного оборудования и при обнаружении неполадок принятие мер по их устранению;</br>\r\n\r\n<br>Проведение планово-предупредительного ремонта оборудования согласно имеющимся планам и графикам, а также сменным заданиям;</br>\r\n\r\n<br>Проведение ремонтных и наладочных работ (включая электросварочные работы).</br>\r\n\r\n<br>Требования:</br>\r\n\r\n<br>Умение читать электрические и пневматические схемы;</br>\r\n\r\n<br>Опыт работы в электрике и механике;</br>\r\n\r\n<br>Как преимущество, опыт проведения сварочных работ.</br>\r\n\r\n<br>Условия:</br>\r\n\r\n<br>Работа в компании-лидере в своей отрасли​;</br>\r\n\r\n<br>Возможности для профессионального и карьерного роста, ведь мы нацелены на развитие сотрудников​;</br>\r\n\r\n<br>Официальное оформление по ТК РФ;</br>\r\n\r\n<br>Конкурентная стабильная заработная плата​;</br>\r\n\r\n<br>Расширенный социальный пакет (ДМС, страхование жизни)​;</br>\r\n\r\n<br>Корпоративные скидки;</br>\r\n\r\n<br>Корпоративная мобильная связь​;</br>\r\n\r\n<br>Возможность гибридного графика работы;</br>\r\n\r\n<br>Широкие возможности для обучения и саморазвития: онлайн-обучение, тренинги, доступ в электронную библиотеку​.</br>', 'от 80 000 руб.'),
(3, 'Инженер наладчик КИПиА', 'Описание вакансии Nutricia (Specialized Nutrition, SN) признанный эксперт в области питания на самых ответственных этапах жизни. Наш портфель это правильное и здоровое питание для малышей в течение первых месяцев жизни, а также для взрослых и детей...', 'Описание вакансии Nutricia (Specialized Nutrition, SN) признанный эксперт в области питания на самых ответственных этапах жизни. Наш портфель это правильное и здоровое питание для малышей в течение первых месяцев жизни, а также для взрослых и детей...\r\n\r\n<br>Какие задачи мы ставим:<br>\r\n\r\n<br>- Контроль над правильной эксплуатацией приборов автоматики, регуляторов, средств измерения;</br>\r\n\r\n<br>- Устранение неполадок в работе оборудования КИПиА;</br>\r\n\r\n<br>- Контроль над правильной эксплуатацией двигателей насосов, электрических шкафов, электропроводки котельной и на производстве;</br>\r\n\r\n<br>- В процессе эксплуатации производить техническое обслуживание и ремонт вышеперечисленного оборудования.</br>\r\n\r\n<br>Что мы от Вас ждем:</br>\r\n\r\n<br>- Техническое образование;</br>\r\n\r\n<br>- Опыт работы слесарем КИПиА, техником КИПиА, наладчиком КИПиА не менее 3 лет на аналогичных производствах;</br>\r\n\r\n<br>- 3 группа электробезопасности;</br>\r\n\r\n<br>- Знание устройств, принципа действия, технических характеристик и конструктивных особенностей обслуживаемого оборудования. Умение читать схемы</br>\r\n\r\n<br>Что мы предлагаем:</br>\r\n\r\n<br>- Официальное оформление в соответствии ТК РФ (отпуск, больничный);</br>\r\n\r\n<br>- Стабильная заработная плата, ее уровень обсуждается с финальным кандидатом;</br>\r\n\r\n<br>- График работы 5/2</br>\r\n\r\n<br>- Работа на современном высокотехнологичном предприятии;</br>\r\n\r\n<br>- Спецодежда предоставляется;</br>\r\n\r\n<br>Дружный коллектив профессионалов.</br>', 'от 110 000 руб.');

